import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent {

}
<style>
    .intro-section{
        text-align: center;
        color: black;
        padding: 20px;
    }

    .search-box {

        display: flex;
        align-items: center;
        justify-content: center;
        margin-bottom: 20px;

    }

    .danhmuc {
    width: 200px;
    }
    .search-input {
    width: 300px;
    background-color: white;
    margin-right: 10px;
    border-color: rgba(255, 255, 255, 0.5);
    color: white;
}

</style>

<div class="container">
    <div class="intro-section">
        <h1>Đây là trang Home</h1>
        <p>Trang này hiển thị danh sách các sản phẩm,ảnh</p>
        <p>Các bạn có thể thể chọn xem sản phẩm và mua hàng tại đây</p>
    </div>
    <!--! o search va combobox-->
    <div class="search-box">
        <input class="search-input" type="text" class="form-control search-input" placeholder="Tìm sản phẩm">
        <select class="form-control danhmuc">
            <option selected disabled>Danh mục sản phẩm</option>
            <option>Sản phẩm hoàn thiện</option>    
            <option>Bộ nguyên liệu tự làm</option>    
            <option>Handmade tặng người yêu</option>    
        </select>
    </div>
</div>
