import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent {

}
<style>
    .intro-section{
        text-align: center;
        color: black;
        padding: 20px;
    }
    .search-box{

    }
</style>

<div class="container">
    <div class="intro-section">
        <h1>Đây là trang Home</h1>
        <p>Trang này hiển thị danh sách các sản phẩm,ảnh</p>
        <p>Các bạn có thể thể chọn xem sản phẩm và mua hàng tại đây</p>
    </div>
    <!--! o search va combobox-->
    <div clas="search-box">
    <input type="text" class="form-control" placeholder="Tìm sản phẩm">
    <select class="form-control">
        <option se>Sản phẩm handmade hoàn thiện</option>
        <option>Handmade tự làm</option>
        <option>Đồ handmade tặng người yêu</option>
    </select>
    </div>
</div>
